-- 文件编码
SET NAMES UTF8;
-- 丢弃数据库BMSL
DROP DATABASE IF EXISTS BMSL;
-- 创建新数据库BMSL
CREATE DATABASE BMSL CHARSET=UTF8;
-- 进入数据库BMSL
USE BMSL;
-- 创建书籍推荐管理表
CREATE TABLE BMSL_broadcast_recommend(
 	-- 创建id编号并设置主键自增
 	recommend_rid INT PRIMARY KEY AUTO_INCREMENT,
 	-- 文章标题头部
 	recommend_title VARCHAR(18),
 	-- 文章创作时间
 	recommend_time DATE;
 	-- 文章作者
 	recommend_author VARCHAR(18),
 	-- 文章内容主体
 	recommend_body VARCHAR(3000),
 	-- 书籍图片
 	recommend_img VARCHAR(80)
 );
 -- 插入数据
INSERT INTO BMSL_broadcast_recommend VALUES(null,"不可不提的日本近代史，从昭和年间到平成时代",2018/12/27,"联合读创","相信大家对《菊与刀》都不陌生。这本书是由美国哥伦比亚大学教授鲁思·本尼迪克特所写，内容主要分析了日本社会和日本民族的“菊与刀”的双重性格。“菊”是日本皇室的象征，代表着忠贞、美好、祥和；而“刀”代表着日本武士道精神，象征着叛逆、尚武、好战。这一对充满矛盾的组合，恰好反映了日本的双重性格所在。我对日本历史的了解便从此开始。后来我有幸结识徐杭老师，对日本历史或者说对日本有了更加深入、多维的了解和认识。徐杭老师比我要年长许多，为人谦和，是一位有着很多不同寻常经历的人，在20岁时有幸接触日本、韩国的文史资料，顿觉东亚近现代史于中国大有参照之益，对日韩文化产生浓厚兴趣，研习十余年。作品见于《环球时报》《旅行家》《读者》等报刊杂志。《平成十二年》是这十几年来徐杭老师研究日本历史的尽心之作。作者以严谨治史的态度和轻松的文学语言，以日本昭和二十年（公元1945年）到平成十二年（公元2000年）为时间节点，介绍了战后初期的日本，经过政治起伏，经济复苏到社会繁荣，最后进入发展瓶颈的历史进程。文中重大事件提要可是梳理为：1945年8月15日，天皇发布《终战诏书》，宣布投降，结束战争。随后，在美国的单独主导下，日本开启了战后改革。1946年1月1日，天皇发表《人间宣言》。宣称自己是人不再是神。日本人在民主化进程中，摆脱了精神的束缚和压制。1946年11月3日，新《宪法》正式颁布，奠定了日本民主的基石，日本失去了发动战争的权力，又称“和平宪法”。时至今日，日本人仍在为捍卫这部宪法而努力。1950-1953朝鲜战争 日本利用这场战争拉动“特需经济”，使国力恢复至战前水平。1951年，日美签署《安保条约》，美军开始长期驻扎日本，为日美同盟打下基础，影响深远。1955年，神武景气，日本迎来战后第一次经济发展高潮！日本人的精神面貌焕然一新。1960年，池田勇人首相提出“所得倍增计划”，即在10年内让国民整体收入增加一倍。日本人生活水平大幅提高，经济腾飞。1973、1979日本度过两次石油危机，成为西方世界仅次于美国的发达国家。日本人精神上摆脱战败阴影。1985年，广场协议签订，日元大幅升值，泡沫经济浮现。1993年，日本政坛波动，泡沫经济破灭，经济发展出现瓶颈。2000年以后，日本自身市场狭小，面临越来越大的国际竞争，但是科技、人才的根底深厚，综合竞争力仍不可小觑。可能有人会说，近代史既没有史前文明的神秘，也没有古代史的深厚，并不值得一谈。但是实则不然，我认为不论是哪一个阶段的历史，都有我们值得借鉴的地方。有时候，反而越是近代的东西越值得借鉴，因为有些现象、问题只有在近代才出现，并非古已有之，或者更加突显。否则便会遇到“一叶障目不见泰山”的问题。我很喜欢作者书中的一句话“中国人研究日本已经不能只看《菊与刀》。日本人解剖中国也不再只盯着《阿Q正传》。”对于历史我们要心存敬畏，并要时刻保持警惕，但是我们不能被思维的高墙包围，日本是一个很善于吸收借鉴的民族。双方都在进步，都在为寻找新方向而努力。2018年《平成十二年》由联合读创出品，在此感谢徐杭老师积极配合我们的工作以及为本书付出的艰辛努力；感谢本书封面设计老师人马·储平。封面以橙黄色为主，突显历史厚重感，兼具视觉亲和力。画面整体是一个太阳升落的过程，既可以看成日出，也可以看成日落。日出日落是自然现象，同时也映照书中日本社会的起起伏伏。公元2000年，是千禧之年，千年一遇。许多人在这一年都有不平凡的经历，国家也是如此。近期有关消息指出，日本将迎来新年号，而 “平成时代”即将被写入历史，承担新的历史使命。我们很幸运在一个新的千年，继续为历史的长河著书立传，希望《平成十二年》在新的千年也会有着不平凡的经历和新的使命。","../image/recommend/n1.jpg")
INSERT INTO BMSL_broadcast_recommend VALUES(null,"生物多样性： 大自然的新陈代谢",2018/12/29,"沧海一粟","老子在《道德经》中曾经说过：道生一，一生二，二生三，三生万物。这是一句穿越古今的成言，道出了宇宙万物诞生和发展的规律——7个基本音符，在音乐家手中却可以演奏出于变万化的美妙旋律；0和1，计算机系统工程师可以利用它编写出复杂的操作系统；26个英文字母，在文学硬汉海明威的手中它们可以排列出鸿篇巨著《丧钟为谁而鸣》；基本粒子，在物理学家看来是构成庞大宇宙中一切物质的基础；ATGC, 脱氧核苷酸只有这四种类型。许多脱氧核苷酸按一定顺序构成DNA。就是这些DNA承载着自然界几乎所有生物体的遗传信息。仅仅ATGC编码的DNA是如何承载这么大的遗传信息量的呢？试想一下，1个核苷酸有4种可能的变化(A, T, G和C) , 倘若—条DNA序列只包含两个核苷酸(AA, AG, AC, AT, GA, GG……TT) , 一共便有16种组合方式(4的2次方)……假设—段DNA亨列中有100,000个脱氧核苷酸对，其中只有100个是可变的，就有4的100次方种基因组类型，其变化形式就有1.61乘以10的60次方，这简直是个天文数字。然而，这仅仅是单核苷酸变异的可能性，生物体内还其他形式的结构变异，这种变化可能性就更多了。这所能够承载的遗传信息量将更加庞大，因此我们无须怀疑DNA的信息承载量，有了这个做基础，才保证了生物的多样性。诞生：从一片沼泽地开始生物多样性，包括三方面：遗传多样性，物种多样性和生态系统多样性。笔者最近一直在思考—个问题，就是如何将生物多样性的这三个方面以一个递进的方式呈献给大家。查阅了大晕资料寻找例证，结果总觉得缺点什么，辗转反侧，最后还是决定基于假设的方式建立一个模型吧。暂且相信一次宇宙生命论，即“地球上最初的生命来自宇宙间的其他星球”。远古时代，一块陨石闯入地球的大气层，坠入某沼泽环境。陨石中携带了一种来自外太空的生命。假设该物种的基因组大小为120M, 也就是 120,000,000个脱氧核苷酸对，其中有99%的序列是保守不变的，那么其余可变换的区域也有1.2M之多，如前所述，仅仅计算单核苷酸变异的遗传信息承载量，结果就是4的1.2M次方，这个数字估计用现在最快的计算机也要算上一段时间。数代繁殖之后，该物种的种群渐渐庞大。繁殖保证了该物种的遗传信息能够代代相传，而变异也在此过程中不断发生。倘若个体发生变异不能适应环境，甚至是直接致死性的，这种变异就几乎不可能被保留下来；反之，如果变异是无关紧要的，不影响该生物的繁殖，甚至更有利于在此环境中生存，则这种变异会顺利的流传到下一代。因此，这些变异一代一代传了下来，该种群在繁衍了许多代以后，就形成了多样化的群体。又过了若干年，该物种的种群数量已经达到一定规模，并从沼泽环境向周边陆地扩散。个体间的基因组并不相同，他们表现出适应新环境的能力也不尽相同，大部分个体在新环境下因不适应环境而死亡，而存活下来的少数个体开始在新的环境下繁衍生息，遗传并变异着，同时继续向周边扩散。地理环境是不断变化的，加之生物群体持续向新坏境扩散，导致新环境下形成的群体逐渐与原来的群体渐行渐远，进化使它们携带着相对稳定的适应新环境的基因，新的群体开始发展壮大，而与原来群体的基因组比，新群体的基因组除了一些比较保守的功能区域之外，已经基本面目全非，它们自己也许不知道，自己已经是一个新的物种了。当这些新的物种，再回到原来的发源地时，它们已经不能融入原来的群体，因为通常不同物种之间是不能发生基因交流的。一个很长历史时期的进化，物种就这样重复着上述过程，“一生二，二生三， 三生万物，”形成了多样的生物——仙人掌不易散失水分而适应了干旱的沙漠环境，猎豹因其奔跑速度快而适应了没有太多障碍物的草原；秏牛因为心肺功能强大能在高原上繁衍生息；企鹅有着厚厚的皮下脂肪层来抵御南极的寒冷，甚至某些细菌可以适应100℃以上的温泉环境……我们的地球因此勃勃生机。世界上没有完全相同的两片叶子，就连同一个物种的种群内的基因组也是变化万千，这为后来的物种分化奠定了基础，也成就了大自然的生物多样性。物种的新陈代谢自然环境的变化促进了物种的进化，但也成为物种灭绝的罪魁祸首。迄今为止全世界发生过5次超大型的大灭绝事件。因为全球性的环境变化，气候突变、沙漠范围扩大、火山爆发、大气成分的改变，整科，整目甚至整纲的生物在很短的时间内， 彻底消失或仅有极少数残存下来。物种消失后就无法再生，只有深埋在地下的化石告诉我们：鹦鹉螺、无颌鱼、三叶虫、恐龙、剑齿虎……， 这世界，他们也曾来过。不过值得庆幸的是，每次生物大灭绝之后， 劫后余生的一些物种又会分化出一些适应新环境的新物种，并会迅速丰富地球上的物种多样性，生物圈依然会再度繁荣。达尔文的观点认为 “物竞天择，适者生存”。两个物种如果对空间资源需求相近，往往因竞争排斥原理而难以长期共存，除非空间和资源十分丰富。自然条件下，资源总是有限的，更具竞争优势，更适应环境的生物往往会更易于生存，而处于劣势者则会被淘汰，从而造成某些物种种群的衰退或消失。进化导致的是两个有亲缘关系的物种去占据不同的生存空间和资源，减少竞争的机会。以古猿为例， 最开始猿类都是生活在森林里，后期因为森林面积的减少， —部分古猿在来到草原上。最终森林的古猿进化成了大猩猩、黑猩猩、长臂猿等，而草原上的古猿进化成人类。愚蠢的行为以上所述物种灭绝原因是在自然状态下的自生自灭，这是生物圈中正常的物种新陈代谢过程，并不可怕。因为还有更可怕的，——人类活动对物种的严重影响。自大航海时代以来，地理大发现把世界各大洲联系在一起，把文明带到世界各地的同时，也带来了灾难。作为食物链最顶端的人类可以是任何动植物的天敌，直接参与生物链，打破生态平衡。蒙古草原上， 狼吃羊和马，也吃田鼠野兔和黄羊，田鼠野兔黄羊等又吃草，草又是羊和马的主要粮食，本来这些生物形成了环环相扣的食物链，它们相互制约相互促进，与草原共同生存了几万年。有一天，蒙古人来到了草原，觉得狼是牛羊的天敌，就采用了各种方法消灭狼，以保护他们的牛羊，但是他们却没有注意到狼对于草原也是有利的， 因为狼也吃田鼠、野兔和黄羊等草原上的大害，限制了田鼠、 黄羊的数量，保住了绿草，牛羊有充足的食物来源。人类的疯狂杀戮使得狼群销声匿迹，而田鼠、野兔和黄羊等大量繁殖，将一大片一大片的绿草吃光，经常的将草连根拔起，最终破坏了草原，许多地方演变成沙漠，而牛羊的数量也急剧减少。看似聪明实则愚蠢的人们，不但破坏了食物链，最终也破坏了自己生活的美好家园。另一经典案例发生在16世纪后期。带着来复枪和猎犬的欧洲人来到了毛里求斯，渡渡鸟成了他们主要的食物来源， 大量的渡渡鸟被捕杀。1681年，最后—只渡渡鸟被残忍的杀害了。从此，地球上再无渡渡鸟了，它们只存在于博物馆的标本室和画家的图画中。渡渡鸟灭绝后，毛里求斯特产的一种珍贵的大颅榄树也渐渐稀少，似乎患上了不孕症。本来渡渡鸟是喜欢在大颅榄树的林中生活，在渡渡鸟经过的地方，大颅榄树总是繁茂，幼苗笠壮。 到了20世纪80年代，毛里求斯只剩下13株大颅榄树，好在科学家最终揭开了渡渡鸟和大颅榄树之间的关系之谜， 找到吐绶鸡来代替渡渡鸟的职能，才使得这种树木绝处逢生。生物多样性的另—大杀手则是生物入侵，尽管生物入侵过程中人类并没有直接参与破坏生物链， 但是人类活动在生物入侵过程中起到了导火索的作用，有意或者无意的引进种植外来物种，使得这些外来物种在当地没有了天敌制约，生物链被破坏，进而导致生态系统失衡。1859年，溃大利亚的一个农夫为了打猎而从外国弄来几只兔子后，一场可怕的生态灾难拉开了序幕。兔子是出了名的快速繁殖者，在澳大利亚没有天敌。由于数量不断翻番，它很快就开始毁坏庄稼。到1950年，澳大利亚的兔子的数量从最初的五只增加到了五亿只，这个国家绝大部分地区的庄稼或草地都遭到了极大损失， —些小岛甚至发生了水土流失。绝望之中，人们从巴西引入了多发黏液瘤病，搞起了针对兔子的细菌战， 结果证明这只是使不断恶化的状况得到暂时缓解， 一小部分兔子对这种病毒具有天然的免疫能力，它们在侥幸逃生后又快速繁殖起来。整个20世纪中期，澳大利亚的灭兔行动从未停止过。硬道理中的硬道理事实上，人类行为对生态系统的影响远不止这些，更加可怕的是环境的污染导致的生态多样性降低。自19世纪工业革命以来，全球变暧，废物排放，化学药剂的使用，植被破坏，沙漠化等环境问题，导致的物种灭绝现象至今仍在上演。今天，人类所谓的建设导致大批的森林、草原 、河流消失了，取而代之的是农田、 工厂、臭水沟……，正如韩寒的小说《一座城池》里所描述：“……我们开车出发，经过表面繁荣的工业区。一座座巨大的工厂分布在路的两边，巨大的烟囱排出五颜六色的气体，将天空点缀得如节日般喜庆。工厂排出的彩色的水让周围的河道也绚丽缤纷，和天空相映成趣，鱼儿纷纷欣喜地浮出水面感受改革开放的春风，空气的味道都和别的地方不一样。在四车道的大路上，卡车欢快地直冒黑烟，运输着生产物资，轿车也欢快地拉着警报，载着来视察的领导。真是一派欣欣向荣的景象。……”这种“欣欣向荣”能够持续多久呢？亦如韩寒所说，只是”表面繁荣” 。据统计，全世界每天有75个物种灭绝，每小时有3个物种灭绝。英国一支科研团队在近期出版的《科学》杂志上发表的英国野生动物调查报告称，在过去40年中，英国本土的鸟类种类减少了54%，本土的野生植物种类减少了28%，而本土蝴蝶的种类更是惊人地减少了71%。一直被认为种类和数量众多，有很强恢复能力的昆虫也开始面临灭绝的命运。甚至有人认为：自工业革命开始，地球就已经进入了第六次物种大灭绝时期。马克思说，存在即是合理。这句话也可以反过来说，灭亡也是合理的。其中关键之处，在于这个“理”，也就是自然规律。维持生态系统平衡，保护好环境，使人类能走的更远。如果与“理”背道而驰的话，所谓的“第六次物种大灭绝时期＂的到来也是合理的，绝不是耸人盺闻。其实，老子的《道德经》中还有一句话: '人法地， 地法天，天法道，道法自然。”可以说这与开篇的那句话形成了一副绝妙的对联。如果说上联是宇宙万物诞生和发展的规律，那么这句则是对人类生存法门的完美浓缩：我们还是一切尊重自然规律。发展是硬道理，但因为发展而破坏了我们赖以生存的家园，得不偿失啊。所以善待环境，保护大自然则是硬道理中的硬道理。","../image/recommend/n2.jpg")
INSERT INTO BMSL_broadcast_recommend VALUES(null,"读书感悟（八）——《班门.光》",2018/12/11,"冰娉","我们不曾体会过失明的滋味，也就无从想象，置身于没有光的世界是什么感觉。可是，这熟悉的“光”，这本书里的“光”，是什么？光意味着温暖、神圣，驱走了黑暗、寒冷与内心的恐惧，人类一路走来的历史，就是一个追逐光明的过程。中国神话里有夸父追日，西方传说里有普罗米修斯盗取火种驱散阴暗。有了光，便有了世间万物的生长，从古至今，人类从未停止过对光的追逐，也从未停止尝试让光增添生活的色彩。老子，在他所著的《道德经》中说：“凿户牖以为室，当其无，有室之用。”可见但凡屋子，都有门窗，供人们出入与采光。对于能看到光明的人来说，它普通得每天环绕在我们周围，我们熟悉得不需要去思考它。对于一个小小开关就能满室光明的现代人，我们甚至不需要去考虑如何驱赶屋里的阴暗。从古至今，人类房屋建造经历了漫长而复杂的过程。如果让时光倒流，把目光投向遥远的古代，采光与居住都与自然环境有关。北方掘土为穴，南方构木为巢，但是门窗无一例外，都是向着南方敞开。为了冬天能抵抗寒冷，向着南方敞开的门窗，能让更多的光与热射入屋内，同时也能让光更长时间保留。如果让时光倒流，把目光投向国外哥特式建筑，就可以知道，当时虔诚的信徒，是如何让教堂里通过门窗采光并呈现天国之光的。这类建筑中的窗，不仅仅只是在墙上开一洞，而是柱子两跨之间的所有面积，都是窗户，或镶上透明玻璃形成墙体，直接将阳光引入教堂；或镶上彩色玻璃，将阳光变得五彩斑斓，仿佛一条通向天国的路铺在脚下。不但驱散了教堂里的阴暗，还让信徒们感觉到“上帝就是光”。墨子，将最早的光学记载，记录了在他所著的《墨经》中，“景，日之光反烛人，则景在日与人之间”,“一张幕布，一盏灯，两手对舞百万兵”——这是最早运用光的“电影”。每当电影院里有大片上映时，年轻人便蜂拥而至。而年轻人也绝对想不到，在县城乡村，古老的皮影戏也同样有着大量的“粉丝”，每每有戏上映，戏迷们就从四面八方赶到演出地点，或带着干粮，或从周围小贩手里买一些儿零嘴，等着好戏上演。老戏班子从前走村串巷，逢有婚丧嫁娶，逢年过节，一个演唱加操控，一个配乐，就是一台好戏。随着城市的发展，乡村越来越多人往外走，老戏班子，也不得不随着大流走入城里，在工厂工人们午休时，演上一段。而当这些工人已经不再工作，回到家乡，皮影戏馆，又成了他们打发时间的地方。看着戏里人物轮番登场，开心一笑，暂时把子女远离，呆在屋里的索然无味，稍稍忘却。当合上这本书最后一页，书里一个个故事里，那种源于光所带来的感知，甚至有些像幻想。这本书以门为界，门里的光，是大师们智慧之光；门外的光，是年轻人对光的体验；而门，就是那扇，为了呈现因为对光有所感知，而创造出来的，融入了奇思妙想的建筑。人类依靠光去感知这个世界，创造出在屋子里也能感受阳光的建筑，设计出透露着身份地位的门窗，利用光创造朦胧的戏剧艺术；植物虽没有眼睛，因为有了温暖，也会努力穿透泥土或植被，向上攀延争取光照的权利；虔诚的信徒站在教堂玻璃穹顶下，即便闭上眼睛也能在眉间感受天堂的暖光，心灵信仰的路上，不曾缺少引路的光。在这世间，从来没有什么完全阴暗的角落，其实每个生命，都是追逐光明的“夸父”。去看光，照进内心。（本文作者为朝闻道写作社群成员）","../image/recommend/n3.webp")